head.script:
     //
     /**
* jCarouselLite - jQuery plugin to navigate images/any content in a carousel style widget.
* @requires jQuery v1.2 or above
*
* http://gmarwaha.com/jquery/jcarousellite/
*
* Copyright (c) 2007 Ganeshji Marwaha (gmarwaha.com)
* Dual licensed under the MIT and GPL licenses:
* http://www.opensource.org/licenses/mit-license.php
* http://www.gnu.org/licenses/gpl.html
*
* Version: 1.0.1
* Note: Requires jquery 1.2 or above from version 1.0.1
*/
     (function($) {                                          // Compliant with jquery.noConflict()
       $.fn.jCarouselLite = function(o) {
         o = $.extend({
           btnPrev: null,
           btnNext: null,
           btnGo: null,
           mouseWheel: false,
           auto: null,
           speed: 200,
           easing: null,
           vertical: false,
           circular: true,
           visible: 3,
           start: 0,
           scroll: 1,
           beforeStart: null,
           afterEnd: null
         }, o || {});
         return this.each(function() {                           // Returns the element collection. Chainable.
           var running = false, animCss=o.vertical?"top":"left", sizeCss=o.vertical?"height":"width";
           var div = $(this), ul = $("ul", div), tLi = $("li", ul), tl = tLi.size(), v = o.visible;
           if(o.circular) {
             ul.prepend(tLi.slice(tl-v-1+1).clone())
             .append(tLi.slice(0,v).clone());
             o.start += v;
           }
           var li = $("li", ul), itemLength = li.size(), curr = o.start;
           div.css("visibility", "visible");
           li.css({overflow: "hidden", float: o.vertical ? "none" : "left"});
           ul.css({margin: "0", padding: "0", position: "relative", "list-style-type": "none", "z-index": "1"});
           div.css({overflow: "hidden", position: "relative", "z-index": "2", left: "0px"});
           var liSize = o.vertical ? height(li) : width(li);   // Full li size(incl margin)-Used for animation
           var ulSize = liSize * itemLength;                   // size of full ul(total length, not just for the visible items)
           var divSize = liSize * v;                           // size of entire div(total length for just the visible items)
           li.css({width: li.width(), height: li.height()});
           ul.css(sizeCss, ulSize+"px").css(animCss, -(curr*liSize));
           div.css(sizeCss, divSize+"px");                     // Width of the DIV. length of visible images
           if(o.btnPrev)
             $(o.btnPrev).click(function() {
               return go(curr-o.scroll);
             });
           if(o.btnNext)
             $(o.btnNext).click(function() {
               return go(curr+o.scroll);
             });
           if(o.btnGo)
             $.each(o.btnGo, function(i, val) {
               $(val).click(function() {
                 return go(o.circular ? o.visible+i : i);
               });
             });
           if(o.mouseWheel && div.mousewheel)
             div.mousewheel(function(e, d) {
               return d>0 ? go(curr-o.scroll) : go(curr+o.scroll);
             });
           if(o.auto)
             setInterval(function() {
               go(curr+o.scroll);
             }, o.auto+o.speed);
           function vis() {
             return li.slice(curr).slice(0,v);
           };
           function go(to) {
             if(!running) {
               if(o.beforeStart)
                 o.beforeStart.call(this, vis());
               if(o.circular) {            // If circular we are in first or last, then goto the other end
                 if(to<=o.start-v-1) {           // If first, then goto last
                   ul.css(animCss, -((itemLength-(v*2))*liSize)+"px");
                   // If "scroll" > 1, then the "to" might not be equal to the condition; it can be lesser depending on the number of elements.
                   curr = to==o.start-v-1 ? itemLength-(v*2)-1 : itemLength-(v*2)-o.scroll;
                 } else if(to>=itemLength-v+1) { // If last, then goto first
                   ul.css(animCss, -( (v) * liSize ) + "px" );
                   // If "scroll" > 1, then the "to" might not be equal to the condition; it can be greater depending on the number of elements.
                   curr = to==itemLength-v+1 ? v+1 : v+o.scroll;
                 } else curr = to;
               } else {                    // If non-circular and to points to first or last, we just return.
                 if(to<0 || to>itemLength-v) return;
                 else curr = to;
               }                           // If neither overrides it, the curr will still be "to" and we can proceed.
               running = true;
               ul.animate(
                 animCss == "left" ? { left: -(curr*liSize) } : { top: -(curr*liSize) } , o.speed, o.easing,
                 function() {
                   if(o.afterEnd)
                     o.afterEnd.call(this, vis());
                   running = false;
                 }
               );
               // Disable buttons when the carousel reaches the last/first, and enable when not
               if(!o.circular) {
                 $(o.btnPrev + "," + o.btnNext).removeClass("disabled");
                 $( (curr-o.scroll<0 && o.btnPrev)
                   ||
                   (curr+o.scroll > itemLength-v && o.btnNext)
                   ||
                   []
                  ).addClass("disabled");
               }
             }
             return false;
           };
         });
       };
       function css(el, prop) {
         return parseInt($.css(el[0], prop)) || 0;
       };
       function width(el) {
         return  el[0].offsetWidth + css(el, 'marginLeft') + css(el, 'marginRight');
       };
       function height(el) {
         return el[0].offsetHeight + css(el, 'marginTop') + css(el, 'marginBottom');
       };
     })(jQuery);
     //

//
var relatedTitles=new Array();var relatedTitlesNum=0;var relatedUrls=new Array();var thumburl=new Array();function related_results_labels_thumbs(json){for(var i=0;i<json.feed.entry.length;i++){var entry=json.feed.entry[i];relatedTitles[relatedTitlesNum]=entry.title.$t;try{thumburl[relatedTitlesNum]=entry.gform_foot.url}catch(error){s=entry.content.$t;a=s.indexOf("<img");b=s.indexOf("src=\"",a);c=s.indexOf("\"",b+5);d=s.substr(b+5,c-b-5);if((a!=-1)&&(b!=-1)&&(c!=-1)&&(d!="")){thumburl[relatedTitlesNum]=d}else thumburl[relatedTitlesNum]='http://3.bp.blogspot.com/-zP87C2q9yog/UVopoHY30SI/AAAAAAAAE5k/AIyPvrpGLn8/s1600/picture_not_available.png'}if(relatedTitles[relatedTitlesNum].length>35)relatedTitles[relatedTitlesNum]=relatedTitles[relatedTitlesNum].substring(0,35)+"...";for(var k=0;k<entry.link.length;k++){if(entry.link[k].rel=='alternate'){relatedUrls[relatedTitlesNum]=entry.link[k].href;relatedTitlesNum++}}}}function removeRelatedDuplicates_thumbs(){var tmp=new Array(0);var tmp2=new Array(0);var tmp3=new Array(0);for(var i=0;i<relatedUrls.length;i++){if(!contains_thumbs(tmp,relatedUrls[i])){tmp.length+=1;tmp[tmp.length-1]=relatedUrls[i];tmp2.length+=1;tmp3.length+=1;tmp2[tmp2.length-1]=relatedTitles[i];tmp3[tmp3.length-1]=thumburl[i]}}relatedTitles=tmp2;relatedUrls=tmp;thumburl=tmp3}function contains_thumbs(a,e){for(var j=0;j<a.length;j++)if(a[j]==e)return true;return false}function printRelatedLabels_thumbs(){for(var i=0;i<relatedUrls.length;i++){if((relatedUrls[i]==currentposturl)||(!(relatedTitles[i]))){relatedUrls.splice(i,1);relatedTitles.splice(i,1);thumburl.splice(i,1);i--}}var r=Math.floor((relatedTitles.length-1)*Math.random());var i=0;if(relatedTitles.length>0)document.write('<h1>'+relatedpoststitle+'</h1>');document.write('<div style="clear: both;"/>');while(i<relatedTitles.length&&i<20&&i<maxresults){document.write('<a style="text-decoration:none;margin:0 7px 0px 0;float:left;');if(i!=0)document.write('"');else document.write('"');document.write(' href="'+relatedUrls[r]+'"><img class="related_img" src="'+thumburl[r]+'"/><br/><div style="width:199px;padding:0 0px;color:#333;height:15px;text-align:left;margin:0px 0px; font:16px Oswald; font-weight:normal; line-height:20px;">'+relatedTitles[r]+'</div></a>');if(r<relatedTitles.length-1){r++}else{r=0}i++}document.write('</div>');relatedUrls.splice(0,relatedUrls.length);thumburl.splice(0,thumburl.length);relatedTitles.splice(0,relatedTitles.length)}
